const anchorInView=(function(){"use strict";const anchorLink=document.querySelectorAll('a[href^="#"]:not([href="#"])');const defaults={alignToTop:!0,behavior:'smooth',block:'start',inline:'nearest'};anchorLink.forEach((item)=>{item.addEventListener('click',(e)=>{e.preventDefault();document.querySelector(item.hash).scrollIntoView({alignToTop:defaults.alignToTop,behavior:defaults.behavior,block:defaults.block,inline:defaults.inline})})});return Object.assign(defaults)})()